import React, { useState, useEffect } from 'react';

const Modal = ({ isOpen, onClose, note, onSave }) => {
    // Define state for title and content
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    // Update state when the note prop changes
    useEffect(() => {
        if (note) {
            setTitle(note.title);
            setContent(note.content);
        }
    }, [note]);

    if (!isOpen) return null;

    const handleSave = () => {
        if (note) {
            onSave({ ...note, title, content });
        }
        onClose();
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-[#333333] text-[#E0E0E0] p-8 rounded-lg shadow-lg relative max-w-lg w-full mx-4 md:mx-auto">
                <button
                    className="absolute top-2 right-2 text-red-500 hover:text-red-700"
                    onClick={onClose}
                >
                    Close
                </button>
                <div className="mb-6">
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        className="w-full p-2 bg-[#444444] border border-[#555555] rounded text-[#E0E0E0] mb-4"
                        placeholder="Title"
                    />
                    <textarea
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        className="w-full p-2 bg-[#444444] border border-[#555555] rounded text-[#E0E0E0] h-40 resize-none"
                        placeholder="Content"
                    />
                </div>
                <div className="flex justify-end">
                    <button
                        onClick={handleSave}
                        className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition duration-200"
                    >
                        Save
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Modal;
