import React, { useState } from 'react';

const NoteForm = ({ onAddNote, onCancel }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddNote(title, content);
        setTitle(''); // Clear input fields after adding a note
        setContent('');
    };

    return (
        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
            <div className="flex justify-between items-center">
                <h2 className="text-xl font-bold text-gray-800">Add a New Note</h2>
                <button
                    onClick={onCancel}
                    className="text-red-500 hover:text-red-700 transition duration-200"
                >
                    Close
                </button>
            </div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Title"
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                />
                <textarea
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    placeholder="Content"
                    className="w-full mb-4 p-2 border border-gray-300 rounded h-24"
                />
                <button
                    type="submit"
                    className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition duration-200"
                >
                    Add Note
                </button>
            </form>
        </div>
    );
};

export default NoteForm;
