import React, { useState } from 'react';
import Modal from './Modal';

const NotesContainer = ({ notes, handleDelete }) => {
    const [selectedNote, setSelectedNote] = useState(null); // State to manage selected note
    const [isEditing, setIsEditing] = useState(false);

    const handleNoteClick = (note) => {
        setSelectedNote(note);
    };

    const handleModalClose = () => {
        setSelectedNote(null);
        setIsEditing(false);
    };

    const handleEdit = () => {
        setIsEditing(true);
    };

    return (
        <div className="space-y-4">
            {notes.map((note) => (
                <div
                    key={note.id}
                    onClick={() => handleNoteClick(note)}
                    className="bg-[#333333] p-4 rounded-lg shadow-md cursor-pointer"
                >
                    <div className="flex justify-between items-center mb-2">
                        <h2 className="text-xl font-bold text-[#E0E0E0]">{note.title}</h2>
                        <button
                            onClick={(e) => {
                                e.stopPropagation();
                                handleDelete(note.id);
                            }}
                            className="text-red-500 hover:text-red-700 transition duration-200"
                        >
                            Delete
                        </button>
                    </div>
                    <p className="text-[#B3B3B3]">
                        {note.content.length > 100 ? `${note.content.slice(0, 100)}...` : note.content}
                    </p>
                </div>
            ))}

            {/* Modal for showing selected note */}
            {selectedNote && (
                <Modal onClose={handleModalClose}>
                    <div className="bg-[#222222] p-6 rounded-lg shadow-md">
                        <div className="flex justify-between items-center">
                            <h2 className="text-xl font-bold text-[#E0E0E0]">{selectedNote.title}</h2>
                            <button
                                onClick={handleModalClose}
                                className="text-red-500 hover:text-red-700 transition duration-200"
                            >
                                Close
                            </button>
                        </div>
                        {isEditing ? (
                            // Edit form here (if needed)
                            <div>
                                <textarea
                                    value={selectedNote.content}
                                    className="w-full mt-4 p-2 border border-gray-300 rounded h-24"
                                />
                                <button className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition duration-200 mt-2">
                                    Save
                                </button>
                            </div>
                        ) : (
                            <p className="text-[#B3B3B3] mt-4">{selectedNote.content}</p>
                        )}
                        <button
                            onClick={handleEdit}
                            className="w-full bg-green-500 text-white py-2 rounded hover:bg-green-600 transition duration-200 mt-2"
                        >
                            Edit
                        </button>
                    </div>
                </Modal>
            )}
        </div>
    );
};

export default NotesContainer;
