import React, { useState } from 'react';
import Modal from './Modal'; // Import the Modal component

const NotesContainer = ({ notes, handleDelete }) => {
    const [selectedNote, setSelectedNote] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleNoteClick = (note) => {
        setSelectedNote(note);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setSelectedNote(null);
    };

    return (
        <div className="space-y-4">
            {[...notes].reverse().map(note => (
                <div 
                    key={note.id} 
                    className="bg-[#212121] p-4 rounded-lg shadow-md cursor-pointer"
                    onClick={() => handleNoteClick(note)} // Open the modal on click
                >
                    <div className="flex justify-between items-center mb-2">
                        <h2 className="text-xl font-bold text-[#E0E0E0]">{note.title}</h2>
                        <button
                            onClick={(e) => { 
                                e.stopPropagation(); // Prevent triggering the modal when deleting
                                handleDelete(note.id);
                            }}
                            className="text-red-500 hover:text-red-700 transition duration-200"
                        >
                            Delete
                        </button>
                    </div>
                    <p className="text-[#A0A0A0] overflow-hidden text-ellipsis whitespace-nowrap">
                        {note.content}
                    </p>
                </div>
            ))}

            {/* Modal for displaying the full note content */}
            <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
                {selectedNote && (
                    <div>
                        <h2 className="text-2xl font-bold mb-4">{selectedNote.title}</h2>
                        <p className="text-[#E0E0E0] whitespace-pre-wrap">
                            {selectedNote.content}
                        </p>
                        <button 
                            onClick={() => {/* Add your edit functionality here */}} 
                            className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition duration-200"
                        >
                            Edit
                        </button>
                    </div>
                )}
            </Modal>
        </div>
    );
};

export default NotesContainer;
