import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NoteForm from './NoteForm';
import NoteItem from './NoteItem';

const NotesContainer = () => {
    const [notes, setNotes] = useState([]);
    const [isAdding, setIsAdding] = useState(false); // State to manage visibility of the add note form

    useEffect(() => {
        fetchNotes();
    }, []);

    const fetchNotes = async () => {
        const response = await axios.get('http://localhost:8000/api/notes/');
        setNotes(response.data);
    };

    const handleAddNote = async (title, content) => {
        await axios.post('http://localhost:8000/api/notes/', { title, content });
        fetchNotes();
        setIsAdding(false); // Hide the form after adding a note
    };

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:8000/api/notes/${id}/`);
        fetchNotes(); // Reload the notes after deletion
    };

    return (
        <div className="min-h-screen bg-[#121212] py-5 px-14">
            <h1 className="text-3xl font-bold text-[#E0E0E0] mb-8">Your Notes</h1>

            {/* Notes List Container */}
            <div className="relative max-w-md mx-auto">
                {/* Add Note Button */}
                <button
                    className="absolute top-[-0.5rem] right-[-0.5rem] bg-blue-500 text-white rounded-full shadow-lg hover:bg-blue-600 transition duration-200 flex items-center justify-center text-xl px-3 pb-1"
                    onClick={() => setIsAdding(true)}
                >
                    + Add
                </button>
                <button
                    class="align-middle select-none font-sans font-bold text-center uppercase transition-all disabled:opacity-50 disabled:shadow-none disabled:pointer-events-none text-xs py-3 px-6 bg-[#333333] text-white shadow-md shadow-[#333333]/10 hover:shadow-lg hover:shadow-[#BB86FC]/20 focus:opacity-[0.85] focus:shadow-none active:opacity-[0.85] active:shadow-none rounded-full"
                    type="button">
                    + Add
                </button>
                <button
                    class="text-white relative px-3 justify-center py-1 bg-[#333333] rounded-full text-center transition-all duration-500
            before:absolute before:top-0 before:left-0 before:w-full before:rounded-full before:h-full before:bg-zinc-400 before:transition-all
            before:duration-300 before:opacity-10 before:hover:opacity-0 before:hover:scale-50
            after:absolute after:top-0 after:left-0 after:w-full after:h-full after:opacity-0 after:transition-all after:duration-300 after:rounded-full
            after:border after:border-white/50 after:scale-125 after:hover:opacity-100 after:hover:scale-100"
                >
                    + Add
                </button>

                {/* Add Note Form */}
                {isAdding && (
                    <NoteForm
                        onAddNote={handleAddNote}
                        onCancel={() => setIsAdding(false)}
                    />
                )}

                {/* Notes List */}
                <ul className="space-y-4">
                    {[...notes].reverse().map(note => (
                        <NoteItem key={note.id} note={note} onDelete={handleDelete} />
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default NotesContainer;
