import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Notes = () => {
    const [notes, setNotes] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [showAddNote, setShowAddNote] = useState(false);

    useEffect(() => {
        fetchNotes();
    }, []);

    const fetchNotes = async () => {
        const response = await axios.get('http://localhost:8000/api/notes/');
        setNotes(response.data);
    };

    const handleAddNote = async () => {
        await axios.post('http://localhost:8000/api/notes/', { title, content });
        setTitle('');
        setContent('');
        setShowAddNote(false);
        fetchNotes();
    };

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:8000/api/notes/${id}/`);
        fetchNotes(); // Reload the notes after deletion
    };

    return (
        <div className="p-8 max-w-lg mx-auto">
            <div className="relative">
                <h1 className="text-4xl text-center mb-4">Notes</h1>
                <button
                    className="absolute top-0 right-0 transform translate-x-1/2 -translate-y-1/2 p-2 bg-blue-500 text-white rounded-full shadow-lg hover:bg-blue-600 focus:outline-none"
                    onClick={() => setShowAddNote(!showAddNote)}
                >
                    +
                </button>
            </div>
            {showAddNote && (
                <div className="mb-4">
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        placeholder="Title"
                        className="w-full p-2 mb-2 border border-gray-300 rounded"
                    />
                    <textarea
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        placeholder="Content"
                        className="w-full p-2 mb-2 border border-gray-300 rounded"
                    />
                    <div className="flex justify-end">
                        <button
                            className="bg-green-500 text-white px-4 py-2 rounded shadow hover:bg-green-600 focus:outline-none"
                            onClick={handleAddNote}
                        >
                            Add Note
                        </button>
                        <button
                            className="ml-2 bg-red-500 text-white px-4 py-2 rounded shadow hover:bg-red-600 focus:outline-none"
                            onClick={() => setShowAddNote(false)}
                        >
                            Cancel
                        </button>
                    </div>
                </div>
            )}
            <ul className="grid grid-cols-1 gap-4">
                {[...Array(5)].map((_, index) => (
                    <li
                        key={index}
                        className={`p-4 bg-white rounded-lg shadow-md border ${notes[index] ? '' : 'border-dashed border-gray-400'}`}
                    >
                        {notes[index] ? (
                            <>
                                <h2 className="text-lg font-semibold">{notes[index].title}</h2>
                                <p className="text-gray-600">{notes[index].content}</p>
                                <button
                                    className="mt-2 text-red-500 hover:text-red-600"
                                    onClick={() => handleDelete(notes[index].id)}
                                >
                                    Delete
                                </button>
                            </>
                        ) : (
                            <div className="text-gray-400">Empty Note</div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Notes;
